INSERT INTO `testimonials` (`name`, `email`, `company`, `position`, `rating`, `testimonial`, `avatar`, `is_featured`, `is_published`) VALUES
('Mark Thompson', 'mark@audioengine.com', 'AudioEngine Studios', 'Senior Audio Engineer', 5, 'The ZenBuds Pro are the best wireless earbuds I have ever used. The sound quality is incredible for the price, and they are so comfortable that I can wear them for hours during long recording sessions.', '/uploads/avatars/mark-thompson.jpg', 1, 1),
('Sarah Kim', 'sarah@designco.com', 'Design Co.', 'Creative Director', 5, 'My order from SmartProZen arrived in just two days! The packaging was great and the SmartGlow light looks amazing on my desk. The quality exceeded my expectations. 10/10 would shop again.', '/uploads/avatars/sarah-kim.jpg', 1, 1),
('David Rodriguez', 'david@techstartup.com', 'TechStartup Inc.', 'CEO', 5, 'SmartProZen has become my go-to for all tech accessories. The ProCharge Wireless Stand has been a game-changer for my home office setup. Fast shipping and excellent customer service.', '/uploads/avatars/david-rodriguez.jpg', 1, 1),
('Lisa Chen', 'lisa@fitnesspro.com', 'Fitness Pro', 'Personal Trainer', 5, 'The ZenWatch Sport has revolutionized how I track my workouts and daily activities. The GPS accuracy is spot-on, and the 7-day battery life means I never worry about charging during long training sessions.', '/uploads/avatars/lisa-chen.jpg', 1, 1);
